// 16 Bits
// 低 第1位 被用作指示拼音是否为简写形式
#[derive(PartialEq, Eq, Copy, Clone)]
pub struct PinYin(pub u16);  // NOTE: 稳定后， `pub` 需要改成 `pub(crate)`

impl PinYin {

    // 需要自行移位 ( offset << 1)
    #[inline]
    pub const fn new_unchecked(offset: u16) -> Self {
        Self(offset)
    }

    // 拼音表偏移量
    #[inline]
    pub fn offset(&self) -> u16 {
        self.0 >> 1
    }

    // 声调
    #[inline]
    pub fn tone(&self) -> Tone {
        // Len: 990 + 990 * 4 * 2 = 8910
        let offset = self.offset();
        match offset {
               0 ..=  989 => Tone::Neutral,

             990 ..= 1979 => Tone::First,
            1980 ..= 2969 => Tone::Second,
            2970 ..= 3959 => Tone::Third,
            3960 ..= 4949 => Tone::Fourth,

            4950 ..= 5939 => Tone::First,
            5940 ..= 6929 => Tone::Second,
            6930 ..= 7919 => Tone::Third,
            7920 ..= 8909 => Tone::Fourth,
            _ => unreachable!(),
        }
    }

    // 当前拼音音调标记格式
    #[inline]
    pub fn tone_format(&self) -> ToneFormat {
        let offset = self.offset();
        match offset {
               0 ..=  989 => ToneFormat::Plain,

             990 ..= 1979 => ToneFormat::Mark,
            1980 ..= 2969 => ToneFormat::Mark,
            2970 ..= 3959 => ToneFormat::Mark,
            3960 ..= 4949 => ToneFormat::Mark,

            4950 ..= 5939 => ToneFormat::Number,
            5940 ..= 6929 => ToneFormat::Number,
            6930 ..= 7919 => ToneFormat::Number,
            7920 ..= 8909 => ToneFormat::Number,
            _ => unreachable!(),
        }
    }

    // 元音字母
    pub fn vowel(&self) -> Letter {
        unimplemented!()
    }

    // 携带音调的元音字母
    pub fn tone_mark(&self) -> Letter {
        unimplemented!()
    }

    /// 不带声调以及不使用简写字母的形式
    #[inline]
    pub fn plain(&self) -> Self {
        let offset = self.offset();
        
        let idx = match offset {
               0 ..=  989 => offset,

             990 ..= 1979 => offset - 990 * 1,
            1980 ..= 2969 => offset - 990 * 2,
            2970 ..= 3959 => offset - 990 * 3,
            3960 ..= 4949 => offset - 990 * 4,

            4950 ..= 5939 => offset - 990 * 5,
            5940 ..= 6929 => offset - 990 * 6,
            6930 ..= 7919 => offset - 990 * 7,
            7920 ..= 8909 => offset - 990 * 8,

            _ => unreachable!(),
        };

        Self(idx << 1)
    }
    
    // 转换为简写形式
    #[inline]
    pub fn simplified(&self) -> Self {
        if self.is_simplified() {
            *self
        } else {
            Self(self.0 | 0b0000_0000_0000_0001)
        }
    }
    
    #[inline]
    pub fn is_simplified(&self) -> bool {
        self.0 & 0b0000_0000_0000_0001 == 1
    }

    // 松散数据
    // 声母部分
    #[inline]
    pub fn initials(&self) -> Option<&'static str> {
        match self.consonant() {
            Some(consonant) => Some(consonant.as_str()),
            None => {
                // 支持输出 Y/W
                let s = self.as_str();
                if s.starts_with("j") {
                    Some("j")
                } else if s.starts_with("w") {
                    Some("w")
                } else {
                    None
                }
            }
        }
    }

    // 韵母部分，改写或补写后的形式
    // 不携带声调 以及简写形式字母
    #[inline]
    pub fn finals(&self) -> &'static str {
        // TODO
        let offset = self.offset();

        match offset {
            0 ..= 22 => {
                unimplemented!()
            },
            _ => unimplemented!(),
        }
    }


    // 结构化数据
    // 声母
    #[inline]
    pub fn consonant(&self) -> Option<Initial> {
        const INITIALS_NUM: u16 = 22; // NOTE: 21 + 1 , 添加了一个站位声母
        
        let idx = self.offset() % INITIALS_NUM;
        if idx == 0 {
            return None;
        }

        let idx = idx - 1;
        debug_assert!((idx as usize) < INITIAL_TABLE.len());

        let offset = idx as u8;

        let initial = Initial(offset << 1);

        if self.is_simplified() {
            Some(initial.simplified())
        } else {
            Some(initial)
        }
    }

    // 韵母 (原始形式)
    #[inline]
    pub fn rhyme(&self) -> Rhyme {
        // TODO
        unimplemented!()
    }

    // 重新设置音调
    #[inline]
    pub fn with_tone(&self, tone: Tone) -> Self {
        let tone_format = self.tone_format();

        self.format(tone, tone_format)
    }
    
    // 重新设置音调的标记形式
    #[inline]
    pub fn with_tone_fotmat(&self, tone_format: ToneFormat) -> Self {
        let tone = self.tone();

        self.format(tone, tone_format)
    }

    // 格式化输出
    #[inline]
    pub fn format(&self, tone: Tone, tone_format: ToneFormat) -> Self {
        use crate::tone::ToneFormat::*;
        use crate::tone::Tone::*;

        let plain = self.plain();
        let plain_offset = plain.offset();
        
        let offset = match tone_format {
            Plain => plain_offset,
            Mark => {
                match tone {
                    Neutral => plain_offset,
                    First   => plain_offset + 990 * 1,
                    Second  => plain_offset + 990 * 2,
                    Third   => plain_offset + 990 * 3,
                    Fourth  => plain_offset + 990 * 4,
                }
            },
            Number => {
                match tone {
                    Neutral => plain_offset,
                    First   => plain_offset + 990 * 5,
                    Second  => plain_offset + 990 * 6,
                    Third   => plain_offset + 990 * 7,
                    Fourth  => plain_offset + 990 * 8,
                }
            },
        };

        if self.is_simplified() {
            Self(offset << 1).simplified()
        } else {
            Self(offset << 1)
        }
    }

    #[inline]
    pub fn as_str(&self) -> &'static str {
        // TODO: 处理简写形式 ( 需要为列建立一个对应表 )
        let s = PINYIN_TABLE[self.offset() as usize];
        debug_assert!(s != _________);

        s
    }
}

