#[test]
fn test_format() {
    let s = PinYin::new_unchecked(21 << 1);
    assert_eq!(s.as_str(), "si");
    assert_eq!(s.initials(), Some("s"));

    assert_eq!(s.format(Tone::Second, ToneFormat::Plain).as_str(), "si");
    assert_eq!(s.format(Tone::Second, ToneFormat::Mark).as_str(), "sí");
    assert_eq!(s.format(Tone::Second, ToneFormat::Number).as_str(), "si2");
}


