const INITIALS_NUM: u16 = 22; // NOTE: 21 + 1 , 添加了一个零声母，代表该音节没有声母部分
const PLAIN_ROWS: u16   = 46; // 46 * 22 = 1012
const PLAIN_LEN: u16    = 1012;


// 16 Bits
// 低 第1位 被用作指示拼音是否为简写形式
#[derive(PartialEq, Eq, Copy, Clone)]
pub struct PinYin(pub u16);  // NOTE: 稳定后， `pub` 需要改成 `pub(crate)`

impl PinYin {

    // 需要自行移位 ( offset << 1)
    #[inline]
    pub const fn new_unchecked(offset: u16) -> Self {
        Self(offset)
    }

    // 拼音表偏移量
    #[inline]
    pub fn offset(&self) -> u16 {
        self.0 >> 1
    }

    // 声调
    #[inline]
    pub fn tone(&self) -> Tone {
        // Len: 1012 + 1012 * 4 * 3 = 13156
        let offset = self.offset();

        match offset / INITIALS_NUM / PLAIN_ROWS {
            0 => Tone::Neutral,
            1 => Tone::First,
            2 => Tone::Second,
            3 => Tone::Third,
            4 => Tone::Fourth,

            5 => Tone::First,
            6 => Tone::Second,
            7 => Tone::Third,
            8 => Tone::Fourth,

            9 => Tone::First,
            10 => Tone::Second,
            11 => Tone::Third,
            12 => Tone::Fourth,

            _ => unreachable!(),
        }
    }

    // 当前拼音音调标记格式
    #[inline]
    pub fn tone_format(&self) -> ToneFormat {
        let offset = self.offset();

        match offset / INITIALS_NUM / PLAIN_ROWS {
            0 => ToneFormat::Plain,

            1 => ToneFormat::Mark,
            2 => ToneFormat::Mark,
            3 => ToneFormat::Mark,
            4 => ToneFormat::Mark,

            5 => ToneFormat::Number,
            6 => ToneFormat::Number,
            7 => ToneFormat::Number,
            8 => ToneFormat::Number,

            9 => ToneFormat::Number2,
            10 => ToneFormat::Number2,
            11 => ToneFormat::Number2,
            12 => ToneFormat::Number2,
            _ => unreachable!(),
        }
    }

    // 元音字母
    pub fn vowel(&self) -> Letter {
        unimplemented!()
    }

    // 携带音调的元音字母
    pub fn tone_mark(&self) -> Letter {
        unimplemented!()
    }

    /// 不带声调以及不使用简写字母的形式
    #[inline]
    pub fn plain(&self) -> Self {
        let offset = self.offset();

        let m = offset / INITIALS_NUM / PLAIN_ROWS;
        debug_assert!(m <= 12);

        let idx = offset - PLAIN_LEN * m;

        Self(idx << 1)
    }
    
    // 转换为简写形式
    #[inline]
    pub fn simplified(&self) -> Self {
        if self.is_simplified() {
            *self
        } else {
            Self(self.0 | 0b0000_0000_0000_0001)
        }
    }
    
    #[inline]
    pub fn is_simplified(&self) -> bool {
        self.0 & 0b0000_0000_0000_0001 == 1
    }

    // 松散数据
    // 声母部分
    #[inline]
    pub fn initials(&self) -> Option<&'static str> {
        match self.consonant() {
            Some(consonant) => Some(consonant.as_str()),
            None => {
                // 支持输出 Y/W
                let s = self.as_str();
                match &s[0..1] {
                    "y" => Some("y"),
                    "w" => Some("w"),
                    _   => None,
                }
            }
        }
    }

    // 韵母部分，改写或补写后的形式
    // 不携带声调 以及简写形式字母
    #[inline]
    pub fn finals(&self) -> &'static str {
        // TODO
        let offset = self.offset();
        
        let s = PINYIN_TABLE[offset as usize];
        debug_assert!(s != _________);

        let idx = self.offset() % INITIALS_NUM;
        match idx {
            0 => match &s[0..1] {
                "y" | "w" => &s[1..],
                _         => s,
            },
            15 ..= 17 => &s[2..],
            _         => &s[1..],
        }
    }


    // 结构化数据
    // 声母
    #[inline]
    pub fn consonant(&self) -> Option<Initial> {
        let idx = self.offset() % INITIALS_NUM;
        if idx == 0 {
            return None;
        }

        let idx = idx - 1;
        debug_assert!((idx as usize) < INITIAL_TABLE.len());

        let offset = idx as u8;

        let initial = Initial(offset << 1);

        if self.is_simplified() {
            Some(initial.simplified())
        } else {
            Some(initial)
        }
    }

    // 韵母 (原始形式)
    #[inline]
    pub fn rhyme(&self) -> Rhyme {
        let row = self.plain().offset() / INITIALS_NUM;

        let rhyme = match row {
             0        => Rhyme::I,                // 韵母为 i
             1 ..= 40 => Rhyme( (row - 1) << 1),
            41 ..= 45 => unimplemented!(),        // TODO: 儿化音部分
            _         => unreachable!(),
        };
        
        if self.is_simplified() {
            rhyme.format(self.tone(), self.tone_format()).simplified()
        } else {
            rhyme.format(self.tone(), self.tone_format())
        }
    }

    // 重新设置音调
    #[inline]
    pub fn with_tone(&self, tone: Tone) -> Self {
        let tone_format = self.tone_format();

        self.format(tone, tone_format)
    }
    
    // 重新设置音调的标记形式
    #[inline]
    pub fn with_tone_fotmat(&self, tone_format: ToneFormat) -> Self {
        let tone = self.tone();

        self.format(tone, tone_format)
    }

    // 格式化输出
    #[inline]
    pub fn format(&self, tone: Tone, tone_format: ToneFormat) -> Self {
        use crate::tone::ToneFormat::*;
        use crate::tone::Tone::*;

        let plain = self.plain();
        let plain_offset = plain.offset();

        let offset = match tone_format {
            Plain => plain_offset,
            Mark => {
                match tone {
                    Neutral => plain_offset,
                    First   => plain_offset + PLAIN_LEN * 1,
                    Second  => plain_offset + PLAIN_LEN * 2,
                    Third   => plain_offset + PLAIN_LEN * 3,
                    Fourth  => plain_offset + PLAIN_LEN * 4,
                }
            },
            Number => {
                match tone {
                    Neutral => plain_offset,
                    First   => plain_offset + PLAIN_LEN * 5,
                    Second  => plain_offset + PLAIN_LEN * 6,
                    Third   => plain_offset + PLAIN_LEN * 7,
                    Fourth  => plain_offset + PLAIN_LEN * 8,
                }
            },
            Number2 => {
                match tone {
                    Neutral => plain_offset,
                    First   => plain_offset + PLAIN_LEN * 9,
                    Second  => plain_offset + PLAIN_LEN * 10,
                    Third   => plain_offset + PLAIN_LEN * 11,
                    Fourth  => plain_offset + PLAIN_LEN * 12,
                }
            }
        };

        if self.is_simplified() {
            Self(offset << 1).simplified()
        } else {
            Self(offset << 1)
        }
    }

    #[inline]
    pub fn as_str(&self) -> &'static str {
        // TODO: 处理简写形式 ( 需要为列建立一个对应表 )
        let s = PINYIN_TABLE[self.offset() as usize];
        debug_assert!(s != _________);

        s
    }
}

